import React, { useEffect, useState } from "react";
import AddProduct from "./AddProduct";
import UpdateProduct from "./UpdateProduct";

const Products = (props) => {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [cateButton, setcateButton] = useState(false);
  const [updateCategories, setupdateCategories] = useState(false);
  const [updateItem, setUpdateItem] = useState({
    id: "",
    name: "",
    price: "",
    image: "",
    category: "",
  });
  const [isdelete, setIsDelete] = useState(false);
  const [sureDelete, setSureDelete] = useState(false);
  const [sureDelete2, setSureDelete2] = useState(false);
  useEffect(() => {
    async function fetchDataCategories() {
      setIsDelete(false);
      const response = await fetch(
        "https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/categories.json"
      );
      const data = await response.json();
      const DATA = [];
      for (const key in data) {
        DATA.push({
          id: key,
          name: data[key],
        });
      }
      setCategories(DATA);
    }
    fetchDataCategories();

    //-----------------------------------Products-------------------------------

    async function fetchData() {
      setIsDelete(false);
      const response = await fetch(
        "https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/products.json"
      );
      const data = await response.json();
      const DATA = [];
      for (const key in data) {
        DATA.push({
          id: key,
          name: data[key].name,
          image: data[key].image,
          price: data[key].price,
          category: data[key].category,
        });
      }
      setProducts(DATA);
    }
    fetchData();
  }, [cateButton, updateCategories, isdelete]);
  const addCategoriesHandler = () => {
    setcateButton(true);
  };
  const handleDelete = (catId) => {
    setSureDelete(true);
    if (sureDelete2 === true) {
      fetch(
        `https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/products/${catId}.json`,
        {
          method: "DELETE",
        }
      ).then(() => setIsDelete(true));
      setSureDelete2(false);
      setSureDelete(false)
    }
    else{
     //   setSureDelete(false);
    }
  };
  const handleUpdate = (id, name, price, image, category) => {
    console.log("update", id, name);
    setUpdateItem({
      id: id,
      name: name,
      price: price,
      image: image,
      category: category,
    });
    setupdateCategories(true);
  };

  const handleChange = (event) => {
    console.log(event.target.value);
    async function fetchData() {
      setIsDelete(false);
      const response = await fetch(
        "https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/products.json"
      );
      const data = await response.json();
      const DATA = [];
      if (event.target.value === "All") {
        for (const key in data) {
          DATA.push({
            name: data[key].name,
            image: data[key].image,
            price: data[key].price,
            category: data[key].category,
          });
        }
      }
      for (const key in data) {
        if (data[key].category === event.target.value) {
          DATA.push({
            name: data[key].name,
            image: data[key].image,
            price: data[key].price,
            category: data[key].category,
          });
        }
      }
      setProducts(DATA);
    }
    fetchData();
  };
  const sureDeleteHandler = () => {
    setSureDelete2(true);

  };
  const cancelDeleteHandler = () => {
    setSureDelete2(false);
    setSureDelete(false)
  };
  return (
    <React.Fragment>

      {cateButton && <AddProduct setCatButton={setcateButton} />}
      <select onChange={handleChange}>
        <option value="All">All</option>
        {categories.map((option) => (
          <option key={option.name} value={option.name}>
            {option.name}
          </option>
        ))}
      </select>
      <table>
        <tr>
          <th>Name</th>
          <th>Price</th>
          <th>Category</th>
          <th>Image</th>
          <th>delete</th>
          <th>update</th>
        </tr>
        <tbody>
          {products.map((cat) => {
            return (
              <tr>
                <td>{cat.name}</td>
                <td>{cat.price}</td>
                <td>{cat.category}</td>
                <td>
                  <img height="100px" width="200px" src={cat.image} />
                </td>
                <td>
                  <button onClick={() => handleDelete(cat.id)}>Delete</button>
                </td>
                {sureDelete && (
                  <div>
                    <p>Are you sure to want to delete</p>
                    <button onClick={sureDeleteHandler}>yes</button>
                    <button onClick={cancelDeleteHandler}>no</button>
                  </div>
                )}
                <td>
                  <button
                    onClick={() =>
                      handleUpdate(
                        cat.id,
                        cat.name,
                        cat.price,
                        cat.image,
                        cat.category
                      )
                    }
                  >
                    Update
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <button onClick={addCategoriesHandler}>Add categories</button>
      {updateCategories && (
        <UpdateProduct
          catId={updateItem.id}
          catName={updateItem.name}
          catPrice={updateItem.price}
          catImage={updateItem.image}
          catCategory={updateItem.category}
          updateState={setupdateCategories}
        />
      )}
    </React.Fragment>
  );
};

export default Products;
