import React, { useState, useRef } from "react";
import Header from "./Header";
import Categories from "./Categories/Categories";
import Products from "./Products/Products";
import CartProvider from "../store/CartProvider";
import CartContext from "../store/cart-context";
const Home = (props) => {
    console.log('check admin',props.checkAdmin)
    const [isCategories, setIsCategories] = useState(false);
    const [isProducts, setIsProducts] = useState(false);
    const [productsShow, setProductsShow] = useState([]);
    const amountInputRef = useRef();
    const setCategories = (val) => {
        setIsCategories(val);
    }
    const setProducts = (val) => {
        setIsProducts(val);
    }
    const handleCategory = (catName) =>{
        async function fetchData() {
            const response = await fetch('https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/products.json');
            const data = await response.json();
            const DATA = [];

            for (const key in data) {
                if(data[key].category === catName){
                DATA.push({
                    name: data[key].name,
                    image: data[key].image,
                    price: data[key].price,
                    category: data[key].category
                })
            }

            }
            setProductsShow(DATA);

        }
        fetchData()
    }
    const cartHandler = () => {

    }
    return (
        <React.Fragment>
            <CartProvider>
            <Header
                handleCategory={handleCategory}
                checkAdmin={props.checkAdmin}
                setIsLogin={props.setIsLogin}
                setCategories={setCategories}
                setProducts={setProducts}
                fromLogin={props.fromLogin} />
            {isCategories && <Categories />}
            {isProducts && <Products />}
            <table>
                <tr>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Category</th>
                    <th>Image</th>
                    <th>quantity</th>
                </tr>
                <tbody>
                    {
                        productsShow.map((cat) => {
                            return (
                                <tr>
                                    <td>{cat.name}</td>
                                    <td>{cat.price}</td>
                                    <td>{cat.category}</td>
                                    <td><img height='100px' width='200px' src={cat.image} /></td>
                                    <td><input type="number" min='1' max='5' defaultValue='1' step='1'/></td>
                                    <td><button onClick={cartHandler}>Add to cart</button></td>
                                </tr>
                            )
                        })

                    }</tbody>
            </table>
            </CartProvider>
        </React.Fragment>
    )
}
export default Home;
