import React, { useState } from "react";
import LoginPage from "./components/LoginPage";
import Home from "./components/Home";
import Login from "./components/Login";
import SignUp from "./components/SignUp";
import CartProvider from "./store/CartProvider";
function App() {
  const [isLogin, setIsLogin] = useState(false)
  const [adminLogin, setadminLogin] = useState(false);
  const [isSignUp, setISSignUp] = useState(false)
  const [fromLogin, setFromLogin] = useState(false)
  console.log(isLogin)
  const setLogin = (val) => {
    setIsLogin(val)
  }
  return (
    <div>

      {isSignUp && <SignUp isSignUp={setISSignUp} />}
      {isLogin && !adminLogin && !isSignUp && <Login
        setAdminLogin={setadminLogin}
        setIsLogin={setLogin}
        isSignUp={setISSignUp}
        setFromLogin={setFromLogin} />}
      {isLogin && adminLogin && <LoginPage setIsLogin={setLogin} adminLogin={setadminLogin} />}

      {!isLogin && <Home setIsLogin={setLogin}  checkAdmin={adminLogin} fromLogin={fromLogin}/>}
    </div>
  );
}

export default App;
