import React, {useEffect, useState} from "react";

const Orders = (props) => {
    const [order, setOrder] = useState([]);
    useEffect(()=>{
        async function fetchData() {
            const response = await fetch('https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/orders.json');
            const data = await response.json();
            const DATA = [];
            for (const key in data) {
                DATA.push({
                    email: data[key].email,
                    amount: data[key].grandTotal,
                    details: data[key].details.map((cat,index)=>({
                       name: cat[0].name,
                       price: cat[index].price
                }))

                })
            }
            console.log(DATA.details)
            setOrder(DATA);


        }
        fetchData()
    },[])
    return(
        <React.Fragment>
            <table>
                <tr>
                    <th>Email</th>
                    <th>Grand Total</th>
                    <th>Details</th>

                </tr>
                <tbody>
                    {
                        order.map((cat) => {
                            return (
                                <tr>
                                    <td>{cat.email}</td>
                                    <td>{cat.amount}</td>
                                   {order.map((item)=>{
                                    <td>{item.name}</td>
                                   })}
                                </tr>
                            )
                        })

                    }</tbody>
            </table>
        </React.Fragment>
    )
}
export default Orders;
