import React, {useContext} from "react";
import CartContext from "../store/cart-context";

const Cart = (props) => {
    const cartCtx = useContext(CartContext);
    return(
        <form>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        cartCtx.items.map((cat) => {
                            return (
                                <tr>
                                    <td>{cat.name}</td>
                                    <td>{cat.price}</td>
                                    <td><img height='100px' width='200px' src={cat.image} /></td>                                 
                                </tr>
                            )
                        })

                    }</tbody>
            </table>
            <h3>{cartCtx.totalAmount}</h3>
            <button>Close</button>
            <button>Order</button>
        </form>
    )
}

export default Cart
