import React, { useEffect, useState } from "react";

const Orders = (props) => {
  const [order, setOrder] = useState([]);
  const [detailId, setDetailId] = useState("");
  const [hideDetail, setHideDetail] = useState("");
  const [showDetails, setShowDetails] = useState(false);
  const [onlyDetails, setOnlyDetails] = useState(false);
  useEffect(() => {
    async function fetchData() {
      const response = await fetch(
        "https://react-http-8f78d-default-rtdb.firebaseio.com/ecommerce/orders.json"
      );
      const data = await response.json();
      console.log(data);
      const DATA = [];
      for (const key in data) {
        DATA.push({
          id: key,
          email: data[key].email,
          amount: data[key].grandTotal,
          details: data[key].details,
        });
      }
      console.log(DATA.details);
      setOrder(DATA);
    }
    fetchData();
  }, []);
  console.log("Order: ", order);
  console.log("order details", order.details);

  const detailsHandler = (Id) => {
    console.log("detailid", detailId);
    console.log("hideid", hideDetail);
    if (Id === hideDetail) {
      console.log("true true");
      setDetailId("");
      setHideDetail("");
      setShowDetails(true);
      setonlyDetails(false)
      return;
    }
    setHideDetail(Id);
    console.log("detailId", Id);
    setDetailId(Id);
    setShowDetails(false);
    onlyDetails(true)
    // if((detailId === hideDetail) && detailId !== ''){
    //     console.log('true true')
    //     setDetailId('');
    //     setHideDetail('');
    //     setShowDetails(true);
    // }
  };
  const detailTable = (Id) => {};
  return (
    <React.Fragment>
      <table border="2px">
        <tr>
          <th>Email</th>
          <th>Grand Total</th>
          <th>Details</th>
        </tr>
        <tbody>
          {order.map((cat) => {
            return (
              <tr>
                {!onlyDetails && (
                  <React.Fragment>
                    {" "}
                    <td>{cat.email}</td>
                    <td>{cat.amount}</td>
                    <td>
                      <button
                        onClick={() => {
                          detailsHandler(cat.id);
                        }}
                      >
                        Details
                      </button>
                    </td>
                  </React.Fragment>
                )}

                {!showDetails &&
                  cat.details.map((detail, index) => {
                    {!showDetails && (
                        <React.Fragment>
                          {" "}
                          <td>{cat.email}</td>
                          <td>{cat.amount}</td>
                          <td>
                            <button
                              onClick={() => {
                                detailsHandler(cat.id);
                              }}
                            >
                              Details
                            </button>
                          </td>
                        </React.Fragment>
                      )}
                    return detailId === cat.id ? (
                      <tr>
                        <td>{detail.name}</td>
                        <td>
                          <img height="50px" width="100px" src={detail.image} />
                        </td>
                        <td>{detail.price}</td>
                        <td>{detail.quantity}</td>
                      </tr>
                    ) : (
                      <tr></tr>
                    );
                    //  {setDetailId('')}
                  })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </React.Fragment>
  );
};
export default Orders;
